import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import './mobile-menu.css';

const MobileMenu = ({ navLinks }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={"mobile__menu ${isOpen ? 'open' : ''}"}>
      <div className="menu__icon" onClick={toggleMenu}>
        <span></span>
        <span></span>
        <span></span>
      </div>
      <ul className="menu__items">
        {navLinks.map((item, index) => (
          <li key={index}>
            <NavLink to={item.path} activeClassName="active__link" onClick={toggleMenu}>
              {item.display}
            </NavLink>
          </li>
        ))}
        <li>
          <NavLink to="/profile" activeClassName="active__link" onClick={toggleMenu}>
            Profile
          </NavLink>
        </li>
      </ul>
    </div>
  );
};

export default MobileMenu;
